value.string:
computer = context
 portal = context.getPortalObject()

 software_installation_list = self.getPortalObject().portal_catalog(
 portal_type='Software Installation',
 default_aggregate_uid=context.getUid(),
 validation_state='validated',
 limit=1,
 url_string={'query': portal.portal_catalog.getResultValue(uid=software_release_uid).getUrlString(), 'key': 'ExactMatch'},
 )

 if len(software_installation_list) == 0:
 return 'Destroyed' 

 software_installation = software_installation_list[0].getObject()
 delivery = software_installation.getCausalityValue(portal_type='Purchase Packing List')
 if delivery is None:
 s = software_installation.getSlapState()
 if s == 'start_requested':
 return 'Installation requested'
 else:
 return 'Destruction requested'

 delivery_line = delivery.contentValues(portal_type='Purchase Packing List Line')[0]
 resource = delivery_line.getResource()
 state = delivery_line.getSimulationState()

 if resource == portal.portal_preferences.getPreferredSoftwareSetupResource():
 if state == 'confirmed':
 return 'Installation requested'
 elif state == 'started':
 return 'Installation in progress'
 elif state == 'stopped':
 return 'Installed'
 else:
 return 'Destroyed'
 elif resource == portal.portal_preferences.getPreferredSoftwareCleanupResource():
 if state == 'confirmed':
 return 'Destruction requested'
 else:
 return 'Destroyed'
