value.string:
slave_instance = state_change['object']
 service_relative_url = slave_instance.portal_preferences.\
 getPreferredInstanceHostingResource()
 need_to_create_packing_list = False
 try:
 instance_setup_line = context.SlaveInstance_getInstanceSetupPackingListLine(state_change)
 except ValueError:
 pass
 else:
 if instance_setup_line.getSimulationState() == 'stopped':
 try:
 sale_packing_list_line = context.SlaveInstance_getInstanceHostingPackingListLine(state_change)
 except ValueError:
 need_to_create_packing_list = True
 else:
 if sale_packing_list_line.getSimulationState() == 'stopped':
 # previously it was still in stop in progress state, now it shall be started again
 sale_packing_list_line.getParentValue().deliver()
 if sale_packing_list_line.getSimulationState() == 'delivered':
 need_to_create_packing_list = True

 portal = context.getPortalObject()
 if need_to_create_packing_list:
 tag = "%s_startInProgress" % slave_instance.getUid()
 if (portal.portal_activities.countMessageWithTag(tag) > 0):
 raise ValueError("Slave Instance is currently being started.")
 # lock software instance in transaction
 slave_instance.serialize()
 sale_packing_list = context.SlaveInstance_createSalePackingList(state_change, service_relative_url, tag)
 sale_packing_list.confirm(activate_kw={'tag':tag})
